    border-radius: 10px;
    box-shadow: 2px 2px rgb(56, 55, 55);
    margin: 10px auto;
    min-height: 120px;
    width: 85%;
    display: flex;
    flex-direction: column;
}

.taskTitle {
    font-family: 'Lemonada', cursive !important;
    min-height: 100px;
    margin: 10px auto;
    padding: 5px;
    text-align: center;
}

.taskAssigned{
    min-height: 50px;
    padding: 5px 15px;
}

.deleteBtn {
    background-color: #4b7bec;
    border: none;
}

.moveBtn {
    background-color: #45aaf2;
    border: none;
}

.containerBtn{
    display: flex;
    justify-content: center;
    margin-bottom: 10px;
}

.newKanban {
    display: flex;
    justify-content: center;
}

@media screen and (max-width: 1200px){
    .contCategory {
        width: 40%;
    }
    #title {
        font-size: 20px;
    }
}

