    height: 100px;
}

.auth-content h2{
	margin: 20px 0;
	color: #333;
	text-transform: uppercase;
	font-size: 2.5rem;
}

.auth-content .input-div{
	position: relative;
    display: grid;
    grid-template-columns: 7% 93%;
    margin: 25px 0;
    padding: 5px 0;
    border-bottom: 2px solid #d9d9d9;
}

.auth-content .input-div.one{
	margin-top: 0;
}

.i{
	color: #a7b1c8;
	display: flex;
	justify-content: center;
	align-items: center;
}

.input-div > div{
    position: relative;
	height: 45px;
}

.input-div > div > input{
	position: absolute;
	left: 0;
	top: 0;
	width: 100%;
	height: 100%;
	border: none;
	outline: none;
	background: none;
	padding: 0.5rem 0.7rem;
	font-size: 1.2rem;
	color: #555;
	font-family: 'poppins', sans-serif;
}

.input-div.pass{
	margin-bottom: 25px;
}

a{
	display: block;
	text-align: right;
}

.formBtn{
	display: block;
	width: 100%;
	height: 50px;
	border-radius: 25px;
	outline: none;
	border: none;
	background-image: linear-gradient(to right, rgb(183, 74, 233), rgb(195,158,212), rgb(183, 74, 233));
	background-size: 200%;
	font-size: 1.2rem;
	color: #fff;
	font-family: 'Poppins', sans-serif;
	text-transform: uppercase;
	margin: 1rem 0;
	cursor: pointer;
	transition: .5s;
}

.formBtn:hover{
	background-position: right;
}

@media screen and (max-width: 1050px){
	.containerForm{
		grid-gap: 5rem;
	}
}

@media screen and (max-width: 1100px){
	form{
		width: 290px;
	}

	.auth-content h2{
        font-size: 2.4rem;
        margin: 8px 0;
	}

	.img img{
		width: 400px;
	}
}

@media screen and (max-width: 900px){
	.containerForm{
		grid-template-columns: 1fr;
	}

	.img{
		display: none;
	}

	.wave{
		display: none;
	}

	.auth-content{
		justify-content: center;
	}
}

a{
	cursor: pointer;
}

